<script>
  (function() {
    class AnimatedHeadingackxrawvrs1drwururaigenblock2c38c12nnbdcb extends HTMLElement {
      constructor() {
        super();
        this.hasAnimated = false;
        this.fireworkColors = [
          '#e5757e',
          '#ffd9d4',
          '#0f3062',
          '#c7cf56'
        ];
        this.enableHeadingAnimation = false;
        this.enableTypingEffect = false;
        this.enableHaloAnimation = false;
      }

      connectedCallback() {
        this.heading = this.querySelector('[data-heading]');
        this.description = this.querySelector('[data-description]');
        this.halo = this.querySelector('[data-halo]');
        this.fireworksContainer = this.querySelector('[data-fireworks]');
        
        if (this.heading) {
          this.wrapWords();
        }

        if (this.description) {
          this.descriptionText = this.description.getAttribute('data-text');
          this.description.textContent = '';
        }

        this.setupIntersectionObserver();
      }

      wrapWords() {
        const text = this.heading.textContent.trim();
        const words = text.split(' ');
        
        this.heading.innerHTML = words
          .map(word => `<span class="ai-animated-heading__word-ackxrawvrs1drwururaigenblock2c38c12nnbdcb">${word}</span>`)
          .join(' ');
        
        this.words = this.heading.querySelectorAll('.ai-animated-heading__word-ackxrawvrs1drwururaigenblock2c38c12nnbdcb');
      }

      setupIntersectionObserver() {
        const options = {
          threshold: 0.2,
          rootMargin: '0px'
        };

        this.observer = new IntersectionObserver((entries) => {
          entries.forEach(entry => {
            if (entry.isIntersecting && !this.hasAnimated) {
              this.animateHeading();
              this.hasAnimated = true;
            }
          });
        }, options);

        this.observer.observe(this);
      }

      animateHeading() {
        if (this.halo && this.enableHaloAnimation) {
          this.halo.classList.add('animate');
        }

        if (this.words) {
          if (this.enableHeadingAnimation) {
            this.words.forEach((word, index) => {
              setTimeout(() => {
                word.classList.add('animate');
              }, index * 0);
            });
          } else {
            this.words.forEach(word => {
              word.classList.add('visible');
            });
          }
        }

        if (this.fireworksContainer) {
          this.startFireworks();
        }

        if (this.description && this.descriptionText) {
          if (this.enableTypingEffect) {
            const totalWordDelay = this.words && this.enableHeadingAnimation ? this.words.length * 0 : 0;
            const animationDuration = this.enableHeadingAnimation ? 500.0 * 0.5 : 0;
            const startDelay = totalWordDelay + animationDuration;
            
            setTimeout(() => {
              this.typeText();
            }, startDelay);
          } else {
            this.description.textContent = this.descriptionText;
          }
        }
      }

      startFireworks() {
        const particlesPerBurst = 12;
        const burstCount = 4;
        const interval = 1000;

        for (let burst = 0; burst < burstCount; burst++) {
          setTimeout(() => {
            this.createFireworkBurst('left', particlesPerBurst);
            this.createFireworkBurst('right', particlesPerBurst);
          }, burst * interval);
        }
      }

      createFireworkBurst(side, particleCount) {
        const containerHeight = this.offsetHeight;
        const launchY = containerHeight * 0.6;

        for (let i = 0; i < particleCount; i++) {
          const particle = document.createElement('div');
          particle.className = `ai-animated-heading__firework-ackxrawvrs1drwururaigenblock2c38c12nnbdcb ${side}`;
          
          const angle = (Math.PI * 2 * i) / particleCount;
          const distance = 40 + Math.random() * 40;
          const x = Math.cos(angle) * distance;
          const y = Math.sin(angle) * distance;
          
          particle.style.setProperty('--ai-firework-x', `${x}px`);
          particle.style.setProperty('--ai-firework-y', `${y}px`);
          particle.style.top = `${launchY}px`;
          particle.style.backgroundColor = this.fireworkColors[Math.floor(Math.random() * this.fireworkColors.length)];
          particle.style.animationDelay = `${Math.random() * 0.2}s`;
          
          this.fireworksContainer.appendChild(particle);
          
          setTimeout(() => {
            particle.classList.add('animate');
          }, 10);

          setTimeout(() => {
            particle.remove();
          }, (1.5 * 1000) + 200);
        }
      }

      typeText() {
        let charIndex = 0;
        const typingSpeed = 60;
        
        this.description.classList.add('typing');
        
        const typeInterval = setInterval(() => {
          if (charIndex < this.descriptionText.length) {
            this.description.textContent = this.descriptionText.substring(0, charIndex + 1);
            charIndex++;
          } else {
            clearInterval(typeInterval);
            this.description.classList.remove('typing');
          }
        }, typingSpeed);
      }

      disconnectedCallback() {
        if (this.observer) {
          this.observer.disconnect();
        }
      }
    }

    customElements.define('animated-heading-ackxrawvrs1drwururaigenblock2c38c12nnbdcb', AnimatedHeadingackxrawvrs1drwururaigenblock2c38c12nnbdcb);
  })();
</script>




